O cities (id, capital, name, population, country_id) VALUES(2112, true, 'BRAZZAVILLE', 1292000, 1408);UPDATE countries SET capitol_id=2112 WHERE id=1408;INSERT INTO countries (id, background, fullName, gdpPerCapita, gdpTotal, latitude, latitudeType, location, longitude, longitudeType, name, capitol_id) VALUES(1409, 'Established as a Belgian colony in 1908, the then-Republic of the Congo gained its independence in 1960, but its early years were marred by political and social instability. Col. Joseph MOBUTU seized power and declared himself president in a November 1965 coup. He subsequently changed his name - to MOBUTU Sese Seko - as well as that of the country - to Zaire. MOBUTU retained his position for 32 years through several sham elections, as well as through brutal force. Ethnic strife and civil war, touched off by a massive inflow of refugees in 1994 from fighting in Rwanda and Burundi, led in May 1997 to the toppling of the MOBUTU regime by a rebellion backed by Rwanda and Uganda and fronted by Laurent KABILA. He renamed the country the Democratic Republic of the Congo (DRC), but in August 1998 his regime was itself challenged by a second insurrection again backed by Rwanda and Uganda. Troops from Angola, Chad, Namibia, Sudan, and Zimbabwe intervened to support KABILA\'s regime. A cease-fire was signed in July 1999 by the DRC, Congolese armed rebel groups, Angola, Namibia, Rwanda, Uganda, and Zimbabwe but sporadic fighting continued. Laurent KABILA was assassinated in January 2001 and his son, Joseph KABILA, was named head of state. In October 2002, the new president was successful in negotiating the withdrawal of Rwandan forces occupying eastern Congo; two months later, the Pretoria Accord was signed by all remaining warring parties to end the fighting and establish a government of national unity. A transitional government was set up in July 2003. Joseph KABILA as president and four vice presidents represented the former government, former rebel groups, the political opposition, and civil society. The transitional government held a successful constitutional referendum in December 2005 and elections for the presidency, National Assembly, and provincial legislatures in 2006. The National Assembly was installed in September 2006 and KABILA was inaugurated president in December 2006. Provincial assemblies were constituted in early 2007, and elected governors and national senators in January 2007. The next national elections are scheduled for November 2011.', 'Democratic Republic of the Congo', 300, 20960000000, 0.0, 'N', 'Central Africa, northeast of Angola', 25.0, 'E', 'DRC', null);INSERT INTO languages (id, name) VALUES(1761, 'French');INSERT INTO country_languages (country_id, language_id) VALUES(1409, 1761);INSERT INTO religions (id, name, value, country_id) VALUES(197, 'Kimbanguist', 10.0, 1409);INSERT INTO religions (id, name, value, country_id) VALUES(198, 'Muslim', 10.0, 1409);INSERT INTO religions (id, name, value, country_id) VALUES(199, 'Protestant', 20.0, 1409);INSERT INTO religions (id, name, value, country_id) VALUES(200, 'Roman Catholic', 50.0, 1409);INSERT INTO religions (id, name, value, country_id) VALUES(201, 'other (includes syncretic sects and indigenous beliefs)', 10.0, 1409);INSERT INTO cities (id, capital, name, population, country_id) VALUES(2113, true, 'KINSHASA', 8401000, 1409);UPDATE countries SET capitol_id=2113 WHERE id=1409;INSERT INTO cities (id, capital, name, population, country_id) VALUES(2114, false, 'Lubumbashi', 1543000, 1409);INSERT INTO cities (id, capital, name, population, country_id) VALUES(2115, false, 'Mbuji-Mayi', 1488000, 1409);INSERT INTO cities (id, capital, name, population, country_id) VALUES(2116, false, 'Kananga', 878000, 1409);INSERT INTO cities (id, capital, name, population, country_id) VALUES(2117, false, 'Kisangani', 812000, 1409);INSERT INTO countries (id, background, fullName, gdpPerCapita, gdpTotal, latitude, latitudeType, location, longitude, longitudeType, name,